import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withStyles, Box, Grid } from '@material-ui/core';

import {
  getMovies,
  getShowtimes,
  getMovieSuggestion,
  getCinemas
} from '../../../store/actions';

import MovieCarousel from '../components/MovieCarousel/MovieCarousel';
import MovieBanner from '../components/MovieBanner/MovieBanner';
import CinemaCard from '../components/CinemaCard/CinemaCard';

import styles from './styles';

class HomePage extends Component {
  componentDidMount() {
    const {
      movies,
      showtimes,
      suggested,
      getMovies,
      getShowtimes,
      getMovieSuggestion,
      getCinemas,
      user,
      cinemas
    } = this.props;

    if (!movies.length) getMovies();
    if (!showtimes.length) getShowtimes();
    if (!cinemas.length) getCinemas();
    if (user && !suggested.length) getMovieSuggestion(user.username);
  }

  componentDidUpdate(prevProps) {
    if (this.props.user !== prevProps.user && this.props.user) {
      this.props.getMovieSuggestion(this.props.user.username);
    }
  }

  render() {
    const {
      classes,
      randomMovie,
      comingSoon,
      nowShowing,
      suggested,
      cinemas
    } = this.props;

    return (
      <Fragment>
        <MovieBanner movie={randomMovie} height="85vh" />
        <Box height={60} />

        <MovieCarousel
          carouselClass={classes.carousel}
          title="Suggested for you"
          movies={suggested}
        />
        <MovieCarousel
          carouselClass={classes.carousel}
          title="Now Showing"
          to="/movie/category/nowShowing"
          movies={nowShowing}
        />
        <MovieCarousel
          carouselClass={classes.carousel}
          title="Coming Soon"
          to="/movie/category/comingSoon"
          movies={comingSoon}
        />

        <Box className={classes.carousel} mt={5}>
          <h2 style={{ color: '#fff', marginBottom: '16px' }}>Available Cinemas</h2>
          <Grid container spacing={2}>
            {cinemas.map(cinema => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={cinema._id}>
                <CinemaCard cinema={cinema} />
              </Grid>
            ))}
          </Grid>
        </Box>
      </Fragment>
    );
  }
}

HomePage.propTypes = {
  className: PropTypes.string,
  classes: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired,
  movies: PropTypes.array.isRequired,
  latestMovies: PropTypes.array.isRequired,
  cinemas: PropTypes.array.isRequired
};

const mapStateToProps = ({ movieState, showtimeState, authState, cinemaState }) => ({
  movies: movieState.movies,
  randomMovie: movieState.randomMovie,
  latestMovies: movieState.latestMovies,
  comingSoon: movieState.comingSoon,
  nowShowing: movieState.nowShowing,
  showtimes: showtimeState.showtimes,
  suggested: movieState.suggested,
  user: authState.user,
  cinemas: cinemaState.cinemas
});

const mapDispatchToProps = {
  getMovies,
  getShowtimes,
  getMovieSuggestion,
  getCinemas
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(HomePage));
