import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withStyles, Box, Grid, Typography, Button } from '@material-ui/core';
import { Link } from 'react-router-dom';
import {
  getMovies,
  getShowtimes,
  getMovieSuggestion
} from '../../../store/actions';
import MovieBanner from '../components/MovieBanner/MovieBanner';
import styles from './styles';

class HomePage extends Component {
  componentDidMount() {
    const {
      movies,
      showtimes,
      suggested,
      getMovies,
      getShowtimes,
      getMovieSuggestion,
      user
    } = this.props;
    if (!movies.length) getMovies();
    if (!showtimes.length) getShowtimes();
    if (user) {
      if (!suggested.length) getMovieSuggestion(user.username);
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.user !== prevProps.user) {
      this.props.user &&
        this.props.getMovieSuggestion(this.props.user.username);
    }
  }

  render() {
    const {
      classes,
      randomMovie,
      comingSoon,
      nowShowing,
      suggested
    } = this.props;

    return (
      <Fragment>
        <Box className={classes.root}>
          <MovieBanner movie={randomMovie} height="85vh" />
          <Box height={60} />

          <Typography variant="h5" style={{ margin: '20px 0', color: 'white' }}>
            All Movies
          </Typography>
          <Grid container spacing={3}>
            {nowShowing.map(movie => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={movie._id}>
                <Box style={{ background: '#1e1e2f', borderRadius: 8, overflow: 'hidden' }}>
                  <img
                    src={movie.imageUrl || 'https://via.placeholder.com/300x180.png?text=Movie+Image'}
                    alt={movie.title}
                    style={{ width: '100%', height: 200, objectFit: 'cover' }}
                  />
                  <Box p={2}>
                    <Typography variant="h6" gutterBottom style={{ color: 'white' }}>
                      {movie.title}
                    </Typography>
                    <Typography variant="body2" style={{ color: '#ccc' }} gutterBottom>
                      {movie.description?.slice(0, 100)}...
                    </Typography>
                    <Button
                      variant="contained"
                      color="primary"
                      component={Link}
                      to={`/booking/${movie._id}`}
                    >
                      Buy Tickets
                    </Button>
                  </Box>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Fragment>
    );
  }
}

HomePage.propTypes = {
  className: PropTypes.string,
  classes: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired,
  movies: PropTypes.array.isRequired,
  latestMovies: PropTypes.array.isRequired
};

const mapStateToProps = ({ movieState, showtimeState, authState }) => ({
  movies: movieState.movies,
  randomMovie: movieState.randomMovie,
  latestMovies: movieState.latestMovies,
  comingSoon: movieState.comingSoon,
  nowShowing: movieState.nowShowing,
  showtimes: showtimeState.showtimes,
  suggested: movieState.suggested,
  user: authState.user
});

const mapDispatchToProps = { getMovies, getShowtimes, getMovieSuggestion };

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(HomePage));
