// BookingSeats.js
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Box } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  row: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(1),
  },
  seat: {
    width: 36,
    height: 36,
    margin: theme.spacing(0.5),
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontWeight: 600,
    color: '#fff',
    borderRadius: 4,
    cursor: 'pointer',
    fontSize: 12,
  },
  legend: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: theme.spacing(2),
    color: '#fff'
  },
  legendItem: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(2),
    fontSize: 14
  },
  legendColor: {
    width: 16,
    height: 16,
    marginRight: 4,
    borderRadius: 2
  }
}));

const BookingSeats = ({ seats, seatLabels, onSelectSeat }) => {
  const classes = useStyles();

  const getColor = (val) => {
    switch (val) {
      case 0: return '#605DA9'; // Available
      case 1: return '#414246'; // Reserved
      case 2: return '#78CD04'; // Selected
      case 3: return '#0E97DA'; // Recommended
      default: return 'transparent';
    }
  };

  return (
    <Box mt={4}>
      {seats.map((row, rowIndex) => (
        <div key={rowIndex} className={classes.row}>
          {row.map((seat, colIndex) => (
            <div
              key={`seat-${rowIndex}-${colIndex}`}
              className={classes.seat}
              onClick={() => seat !== null && onSelectSeat(rowIndex, colIndex)}
              style={{ backgroundColor: getColor(seat), pointerEvents: seat === null ? 'none' : 'auto' }}
            >
              {seatLabels?.[rowIndex]?.[colIndex] || ''}
            </div>
          ))}
        </div>
      ))}

      {/* Legend */}
      <div className={classes.legend}>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#605DA9' }} /> Available
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#414246' }} /> Reserved
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#78CD04' }} /> Selected
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#0E97DA' }} /> Recommended
        </div>
      </div>
    </Box>
  );
};

export default BookingSeats;
