// Komponen BookingSeats dengan layout custom seperti gambar (grup baris & kolom dinamis, label seperti A1, B1, dst)
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Box, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  row: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(0.5),
  },
  seat: {
    width: 32,
    height: 32,
    margin: 2,
    borderRadius: 4,
    fontSize: 12,
    color: '#fff',
    fontWeight: 'bold',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#605DA9',
    '&.reserved': { backgroundColor: '#414246' },
    '&.selected': { backgroundColor: '#78CD04' },
    '&.recommended': { backgroundColor: '#0E97DA' },
    '&.empty': { visibility: 'hidden' },
  },
  legend: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: theme.spacing(2),
    color: '#eee',
  },
  legendItem: {
    marginRight: theme.spacing(3),
    display: 'flex',
    alignItems: 'center',
  },
  legendColor: {
    width: 12,
    height: 12,
    borderRadius: 2,
    marginRight: 6,
  },
}));

const BookingSeats = ({ seats, onSelectSeat, seatLabels }) => {
  const classes = useStyles();
  const rowLetters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

  return (
    <Box mt={4}>
      {seats.map((row, rowIndex) => (
        <div key={rowIndex} className={classes.row}>
          {row.map((seat, colIndex) => {
            const seatLabel = seatLabels?.[rowIndex]?.[colIndex] || `${rowLetters[rowIndex]}${colIndex + 1}`;
            if (seat === null) return <div key={colIndex} className={`${classes.seat} empty`} />;
            let seatClass = classes.seat;
            if (seat === 1) seatClass += ' reserved';
            else if (seat === 2) seatClass += ' selected';
            else if (seat === 3) seatClass += ' recommended';
            return (
              <div
                key={colIndex}
                className={seatClass}
                onClick={() => onSelectSeat(rowIndex, colIndex)}>
                {seatLabel}
              </div>
            );
          })}
        </div>
      ))}

      {/* Legend */}
      <div className={classes.legend}>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#605DA9' }} /> Available
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#414246' }} /> Reserved
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#78CD04' }} /> Selected
        </div>
        <div className={classes.legendItem}>
          <div className={classes.legendColor} style={{ backgroundColor: '#0E97DA' }} /> Recommended
        </div>
      </div>
    </Box>
  );
};

export default BookingSeats;
