import React from 'react';
import classnames from 'classnames';
import {
  Box,
  Typography,
  Button,
  makeStyles,
  Card,
  CardContent,
  CardMedia
} from '@material-ui/core';
import ArrowRightAlt from '@material-ui/icons/ArrowRightAlt';
import { Link } from 'react-router-dom';
import { textTruncate } from '../../../../utils';

const useStyles = makeStyles((theme) => ({
  card: {
    display: 'flex',
    margin: theme.spacing(2),
    backgroundColor: '#1e1e1e',
    color: 'white',
    borderRadius: 10,
    overflow: 'hidden',
  },
  media: {
    minWidth: 300,
    height: 200,
    objectFit: 'cover',
  },
  content: {
    flex: '1',
    padding: theme.spacing(3),
  },
  movieTitle: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: theme.spacing(1),
  },
  descriptionText: {
    fontSize: '14px',
    marginBottom: theme.spacing(2),
  },
  metaText: {
    fontSize: '13px',
    marginBottom: theme.spacing(1),
    color: '#ccc',
  },
  button: {
    marginTop: theme.spacing(2),
    backgroundColor: '#2196f3',
    color: 'white',
    '&:hover': {
      backgroundColor: '#1976d2',
    },
  },
}));

const MovieCard = ({ movie }) => {
  const classes = useStyles();

  const imageFallback = 'https://source.unsplash.com/featured/?movie';
  const poster = movie.image || imageFallback;

  return (
    <Card className={classes.card}>
      <CardMedia
        component="img"
        className={classes.media}
        image={poster}
        alt={movie.title}
      />
      <CardContent className={classes.content}>
        <Typography className={classes.movieTitle}>
          {movie.title}
        </Typography>
        <Typography className={classes.descriptionText}>
          {textTruncate(movie.description, 200)}
        </Typography>
        <Typography className={classes.metaText}>
          <strong>By:</strong> {movie.director}
        </Typography>
        <Typography className={classes.metaText}>
          <strong>Duration:</strong> {movie.duration} min
        </Typography>
        <Typography className={classes.metaText}>
          <strong>Genre:</strong> {movie.genre}
        </Typography>
        <Link to={`movie/${movie._id}`} style={{ textDecoration: 'none' }}>
          <Button className={classes.button} variant="contained">
            Learn More
            <ArrowRightAlt style={{ marginLeft: 8 }} />
          </Button>
        </Link>
      </CardContent>
    </Card>
  );
};

export default MovieCard;
