// BookingSeats.js
import React, { Fragment } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Box } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  row: {
    display: 'flex',
    justifyContent: 'center',
    width: '100%',
  },
  seat: {
    cursor: 'pointer',
    color: 'white',
    borderRadius: 4,
    padding: theme.spacing(1),
    margin: theme.spacing(0.25),
    fontWeight: 600,
    minWidth: 32,
    textAlign: 'center',
    '&:hover': {
      opacity: 0.8,
    },
  },
  seatInfoContainer: {
    width: '100%',
    margin: 'auto',
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    textAlign: 'center',
    color: '#eee',
  },
  seatInfo: {
    margin: theme.spacing(1),
    display: 'flex',
    alignItems: 'center',
  },
  seatInfoLabel: {
    marginRight: theme.spacing(1),
    display: 'inline-block',
    width: 14,
    height: 14,
    borderRadius: 2,
  },
  [theme.breakpoints.down('sm')]: {
    seat: { padding: theme.spacing(0.75), margin: theme.spacing(0.25) },
    seatInfoContainer: { flexDirection: 'column', alignItems: 'center' },
  },
}));

export default function BookingSeats({ seats = [], labels = [], onSelectSeat }) {
  const classes = useStyles();

  const isValidSeats = Array.isArray(seats) && seats.length > 0 && seats.every(row => Array.isArray(row));

  return (
    <Fragment>
      <Box width={1} pt={5}>
        {isValidSeats ? (
          seats.map((seatRows, rowIndex) => (
            <div key={rowIndex} className={classes.row}>
              {seatRows.map((seat, seatIndex) => {
                const label = labels[rowIndex]?.[seatIndex] || '';
                if (seat === null) {
                  return <Box key={`empty-${rowIndex}-${seatIndex}`} width={32} m={0.5} />;
                }
                return (
                  <Box
                    key={`seat-${rowIndex}-${seatIndex}`}
                    onClick={() => seat !== 1 && onSelectSeat(rowIndex, seatIndex)}
                    className={classes.seat}
                    bgcolor={
                      seat === 1
                        ? 'rgb(65, 66, 70)' // Reserved
                        : seat === 2
                        ? 'rgb(120, 205, 4)' // Selected
                        : seat === 3
                        ? 'rgb(14, 151, 218)' // Recommended
                        : 'rgb(96, 93, 169)' // Available
                    }
                  >
                    {label}
                  </Box>
                );
              })}
            </div>
          ))
        ) : (
          <Box color="white" textAlign="center">Seats not available.</Box>
        )}
      </Box>

      {/* Legend */}
      <Box width={1} mt={6}>
        <div className={classes.seatInfoContainer}>
          <div className={classes.seatInfo}>
            <div className={classes.seatInfoLabel} style={{ background: 'rgb(96, 93, 169)' }} />
            Seat Available
          </div>
          <div className={classes.seatInfo}>
            <div className={classes.seatInfoLabel} style={{ background: 'rgb(65, 66, 70)' }} />
            Reserved Seat
          </div>
          <div className={classes.seatInfo}>
            <div className={classes.seatInfoLabel} style={{ background: 'rgb(120, 205, 4)' }} />
            Selected Seat
          </div>
          <div className={classes.seatInfo}>
            <div className={classes.seatInfoLabel} style={{ background: 'rgb(14, 151, 218)' }} />
            Recommended Seat
          </div>
        </div>
      </Box>
    </Fragment>
  );
}
