// BookingSeats.js
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Box, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  row: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
    marginBottom: theme.spacing(1),
  },
  rowLabel: {
    color: '#fff',
    width: 20,
    marginRight: theme.spacing(1),
    textAlign: 'right',
    fontWeight: 600,
  },
  seat: {
    cursor: 'pointer',
    color: '#fff',
    borderRadius: 4,
    padding: theme.spacing(1),
    margin: theme.spacing(0.5),
    fontWeight: 600,
    fontSize: 12,
    textAlign: 'center',
    minWidth: 32,
    '&:hover': {
      opacity: 0.9,
    },
  },
  hiddenSeat: {
    visibility: 'hidden',
    margin: theme.spacing(0.5),
    minWidth: 32,
  },
  legendContainer: {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    color: '#eee',
    marginTop: theme.spacing(4),
  },
  legendItem: {
    margin: theme.spacing(1, 2),
    display: 'flex',
    alignItems: 'center',
  },
  legendBox: {
    width: 14,
    height: 14,
    marginRight: theme.spacing(1),
    borderRadius: 2,
  },
}));

const getSeatColor = status => {
  switch (status) {
    case 0: return 'rgb(65, 66, 70)';     // Reserved
    case 1: return 'rgb(96, 93, 169)';    // Available
    case 2: return 'rgb(120, 205, 4)';    // Selected
    case 3: return 'rgb(14, 151, 218)';   // Recommended
    default: return 'gray';
  }
};



export default function BookingSeats({ seats = [], onSelectSeat }) {
  const classes = useStyles();

  const isValid = Array.isArray(seats) && seats.length > 0 && seats.every(row => Array.isArray(row.seats));

}
