import React from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';

import PublicLayout from './layouts/PublicLayout';
import AuthLayout from './layouts/AuthLayout';

import HomePage from './pages/Home/HomePage';
import MoviePage from './pages/Movie/MoviePage';
import BookingPage from './pages/Booking/BookingPage';
import CinemasPage from './pages/Cinema/CinemasPage';
import LoginPage from './pages/Login/LoginPage';
import RegisterPage from './pages/Register/RegisterPage';
import ProfilePage from './pages/Profile/ProfilePage';
import NotFoundPage from './pages/NotFound/NotFoundPage';
import { WithLayoutRoute } from './components';

const Routes = () => (
  <Switch>
    {/* Home */}
    <WithLayoutRoute
      exact
      path="/"
      layout={PublicLayout}
      component={HomePage}
    />

    {/* Movie Detail */}
    <WithLayoutRoute
      exact
      path="/movie/:id"
      layout={PublicLayout}
      component={MoviePage}
    />

    {/* Booking Page - Versi Baru */}
    <WithLayoutRoute
      exact
      path="/booking/:id"
      layout={PublicLayout}
      layoutProps={{ withFooter: false }}
      component={BookingPage}
    />

    {/* Booking Page - Versi Lama (masih dipertahankan jika ada link lama) */}
    <WithLayoutRoute
      exact
      path="/movie/booking/:id"
      layout={PublicLayout}
      layoutProps={{ withFooter: false }}
      component={BookingPage}
    />

    {/* Cinemas Page */}
    <WithLayoutRoute
      exact
      path="/cinemas"
      layout={PublicLayout}
      component={CinemasPage}
    />

    {/* Auth Routes */}
    <WithLayoutRoute
      exact
      path="/login"
      layout={AuthLayout}
      component={LoginPage}
    />
    <WithLayoutRoute
      exact
      path="/register"
      layout={AuthLayout}
      component={RegisterPage}
    />

    {/* Profile Page */}
    <WithLayoutRoute
      exact
      path="/profile"
      layout={PublicLayout}
      component={ProfilePage}
    />

    {/* Not Found */}
    <WithLayoutRoute
      path="/404"
      layout={PublicLayout}
      component={NotFoundPage}
    />
    
    {/* Redirect Unknown Routes */}
    <Redirect to="/404" />
  </Switch>
);

export default Routes;
