import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withStyles, Box, Grid, Typography, Button } from '@material-ui/core';
import { Link } from 'react-router-dom';
import { getCinemas } from '../../../store/actions';
import styles from './styles';

class HomePage extends Component {
  componentDidMount() {
    const { cinemas, getCinemas } = this.props;
    if (!cinemas.length) getCinemas();
  }

  render() {
    const { classes, cinemas } = this.props;

    return (
      <Fragment>
        <Box className={classes.root}>
          <Grid container spacing={4} justifyContent="center">
            {cinemas.map(cinema => (
              <Grid item key={cinema._id} xs={12} sm={6} md={4} lg={3}>
                <Box className={classes.card}>
                  <img
                    src={cinema.imageUrl || 'https://via.placeholder.com/300x180.png?text=Cinema+Image'}
                    alt={cinema.name}
                    className={classes.cardImage}
                  />
                  <Box className={classes.cardContent}>
                    <Typography variant="h6" gutterBottom>
                      {cinema.name}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      {cinema.location}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      ðŸ’² {cinema.ticketPrice} â‚¬ per movie
                    </Typography>
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      ðŸŽŸ {cinema.totalSeats || 10} seats Available
                    </Typography>

                    {cinema.movies && cinema.movies.length > 0 ? (
                      <Button
                        variant="contained"
                        color="primary"
                        size="small"
                        component={Link}
                        to={`/booking/${cinema.movies[0]._id}`}
                      >
                        Buy Tickets â†’
                      </Button>
                    ) : (
                      <Button
                        variant="contained"
                        size="small"
                        disabled
                      >
                        No Movies
                      </Button>
                    )}
                  </Box>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Fragment>
    );
  }
}

HomePage.propTypes = {
  classes: PropTypes.object.isRequired,
  cinemas: PropTypes.array.isRequired,
  getCinemas: PropTypes.func.isRequired
};

const mapStateToProps = ({ cinemaState }) => ({
  cinemas: cinemaState.cinemas
});

const mapDispatchToProps = {
  getCinemas
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(HomePage));
