import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withStyles, Box, Grid, Button } from '@material-ui/core';
import { Link } from 'react-router-dom';
import { getCinemas } from '../../../store/actions';
import styles from './styles';
import classNames from 'classnames';
import { Typography, Paper } from '@material-ui/core';
import { EventSeat, AttachMoney } from '@material-ui/icons';

const useStyles = theme => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  card: {
    maxWidth: '100%',
    paddingBottom: theme.spacing(2),
    cursor: 'pointer'
  },
  imageWrapper: {
    height: '200px',
    margin: '0 auto',
    overflow: 'hidden',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover'
  },
  details: { padding: theme.spacing(3) },
  name: {
    fontSize: '18px',
    lineHeight: '21px',
    marginTop: theme.spacing(2),
    textTransform: 'capitalize'
  },
  city: {
    lineHeight: '16px',
    height: theme.spacing(4),
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis',
    color: theme.palette.text.secondary,
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(2)
  },
  stats: {
    display: 'flex',
    alignItems: 'center',
    paddingTop: theme.spacing(1),
    paddingLeft: theme.spacing(3)
  },
  eventIcon: {
    color: theme.palette.text.secondary
  },
  eventText: {
    marginLeft: theme.spacing(1),
    color: theme.palette.text.secondary
  },
  buttonWrapper: {
    display: 'flex',
    justifyContent: 'center',
    paddingTop: theme.spacing(1)
  }
});

class HomePage extends Component {
  componentDidMount() {
    const { cinemas, getCinemas } = this.props;
    if (!cinemas.length) getCinemas();
  }

  renderCinemaCard = (cinema, classes) => {
    const cinemaImage = cinema.image || 'https://source.unsplash.com/featured/?cinema';
    const movieId = cinema.movies && cinema.movies.length > 0 ? cinema.movies[0]._id : null;

    return (
      <Grid item key={cinema._id} xs={12} sm={6} md={4} lg={3}>
        <Paper className={classNames(classes.card)}>
          <div className={classes.imageWrapper}>
            <img
              alt="cinema"
              className={classes.image}
              src={cinemaImage}
            />
          </div>
          <div className={classes.details}>
            <Typography className={classes.name} variant="h6">
              {cinema.name}
            </Typography>
            <Typography className={classes.city} variant="body1">
              {cinema.city}
            </Typography>
          </div>
          <div className={classes.stats}>
            <AttachMoney className={classes.eventIcon} />
            <Typography className={classes.eventText} variant="body2">
              {cinema.ticketPrice} € per movie
            </Typography>
          </div>
          <div className={classes.stats}>
            <EventSeat className={classes.eventIcon} />
            <Typography className={classes.eventText} variant="body2">
              {cinema.seatsAvailable} seats Available
            </Typography>
          </div>
          <div className={classes.buttonWrapper}>
            {movieId ? (
              <Button
                variant="contained"
                color="primary"
                size="small"
                component={Link}
                to={`/booking/${movieId}`}
              >
                Buy Tickets →
              </Button>
            ) : (
              <Button variant="contained" size="small" disabled>No Movies</Button>
            )}
          </div>
        </Paper>
      </Grid>
    );
  };

  render() {
    const { classes, cinemas } = this.props;

    return (
      <Fragment>
        <Box className={classes.root}>
          <Grid container spacing={4} justifyContent="center">
            {cinemas.map(cinema => this.renderCinemaCard(cinema, classes))}
          </Grid>
        </Box>
      </Fragment>
    );
  }
}

HomePage.propTypes = {
  classes: PropTypes.object.isRequired,
  cinemas: PropTypes.array.isRequired,
  getCinemas: PropTypes.func.isRequired
};

const mapStateToProps = ({ cinemaState }) => ({
  cinemas: cinemaState.cinemas
});

const mapDispatchToProps = {
  getCinemas
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(useStyles)(HomePage));
