import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withStyles, Box, Grid } from '@material-ui/core';
import {
  getCinemas
} from '../../../store/actions';
import styles from './styles';

class HomePage extends Component {
  componentDidMount() {
    const { cinemas, getCinemas } = this.props;
    if (!cinemas.length) getCinemas();
  }

  render() {
    const { classes, cinemas } = this.props;

    return (
      <Fragment>
        <Box className={classes.root}>
          <Grid container spacing={4} justifyContent="center">
            {cinemas.map(cinema => (
              <Grid item key={cinema._id} xs={12} sm={6} md={4} lg={3}>
                <Box className={classes.card}>
                  <img
                    src={cinema.imageUrl}
                    alt={cinema.name}
                    className={classes.cardImage}
                  />
                  <Box className={classes.cardContent}>
                    <h2>{cinema.name}</h2>
                    <p>{cinema.location}</p>
                    <p>ðŸ’² {cinema.ticketPrice} â‚¬ per movie</p>
                    <p>ðŸŽŸ {cinema.totalSeats} seats Available</p>
                  </Box>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Fragment>
    );
  }
}

HomePage.propTypes = {
  className: PropTypes.string,
  classes: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired,
  cinemas: PropTypes.array.isRequired
};

const mapStateToProps = ({ cinemaState }) => ({
  cinemas: cinemaState.cinemas
});

const mapDispatchToProps = { getCinemas };

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(HomePage));
